import  React, {Component} from 'react';
import axios from 'axios';

import './css/cusAddProject.css';

export default  class cusAddProject extends  Component{


    constructor(props){
        super(props);

        this.onChangeTittle = this.onChangeTittle.bind(this);
        this.onChangeOwner = this.onChangeOwner.bind(this);
        this.onChangeAmount = this.onChangeAmount.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            tittle: '',
            owner: '',
            amount:'',
            description:'',
            type:'',
            duration:'',
            status:''
        }
    }
    onChangeTittle(e){
        this.setState( {
           tittle: e.target.value
        });
    }
    onChangeOwner(e){
        this.setState( {
            owner: e.target.value
        });
    }
    onChangeAmount(e){
        this.setState( {
            amount: e.target.value
        });
    }
    onChangeDescription(e){
        this.setState( {
            description: e.target.value
        });
    }
    onChangeType(e){
        this.setState( {
            type: e.target.value
        });
    }
    onChangeDuration(e){
        this.setState( {
            duration: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        this.state.status = "pendding";
        const obj = {
            tittle : this.state.tittle,
            owner : this.state.owner,
            amount : this.state.amount,
            description : this.state.description,
            type : this.state.type,
            duration : this.state.duration,
            status : this.state.status
        };

        
                        axios.post('http://localhost:4000/zoo/addproject',obj)
                        .then(res => {
                            alert("Project Add Successfully");
                            this.setState({
                                tittle: '',
                                owner: '',
                                amount:'',
                                description:'',
                                type:'',
                                duration:''
                    
                            })
                            console.log(res.data)});
                        // this.props.history.push('/signIn');
                        window.location.replace('/');
                   
    }

    render() {
        return(

            <div className='cusadd'>
               
               <div className='top'>
                    <div className='tittle'>
                           <h2>Animal Haven</h2>
                    </div>
                    <div className='item'>
                        <ul>
                            <li><a href = "/">Home</a></li>
                            <li><a href = "">Booking</a></li>
                            <li><a href = "">Animals</a></li>
                            <li><a href = "">Zoo Projects</a></li>
                            <li><a href = "">Contact Us</a></li>
                            <li><a href = "">About Us</a></li>
                            <li><a href = "">Profile</a></li>
                        </ul>
                    </div>
                </div>
                <br/>
                <hr/>

                <div class="content">
                    <div className="container " style={{marginTop:10}}>
                        <h3 className="text-center" style={{borderBottom:'tomaato solid'}}>Add New Project</h3>
                        <hr/>
                        <br/>
                         <center>
                            <img src = "https://cdn-cashy-static-assets.lucidchart.com/marketing/blog/2017Q3/phases-project-management-lifecycle/phases-project-management-lifecycle-header.png" width ="350" height="200"/>
                         </center>
                        <br/>
                        <hr/>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label>Project Tittle :</label>
                                <input type ="text" required  className="form-control" value={this.state.tittle} onChange = {this.onChangeTittle}/>
                            </div>
                            <div className="form-group">
                                <label>Project Owner :</label>
                                <input type ="text" required className="form-control" value={this.state.owner} onChange = {this.onChangeOwner}/>
                            </div>
                            <div className="form-group">
                                <label>Budgeted Amount (Rs.) :</label>
                                <input type ="number" min = "10000" required className="form-control" value={this.state.amount} onChange = {this.onChangeAmount}/>
                            </div>
                            <div className="form-group">
                                <label>Description :</label>
                                <textarea value={this.state.description} onChange = {this.onChangeDescription} className="form-control"></textarea>
                            </div>
                            <div className="form-group">
                                <label>project Type :</label>
                                <input type ="text" required className="form-control" value={this.state.type} onChange = {this.onChangeType}/>
                            </div>
                            <div className="form-group">
                                <label>Project Duration (Days):</label>
                                <input type ="number" required className="form-control" value={this.state.duration} onChange = {this.onChangeDuration}/>
                            </div>

                        
                            <div className="form-group">
                                <input type = "submit" value = "Add Project" className="btn btn-dark"/>
                            </div>
                        </form>
                    </div>
                    <br/>
                    <hr/>
                    <hr/>
                    <br/>
                </div>
            </div>
        )
    }
}
